package codeupstandard100;

import java.util.Scanner;

public class num89 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sn=new Scanner(System.in);
		int a=sn.nextInt();
		int d=sn.nextInt();
		int n=sn.nextInt();
		
		int result=0;
		result = a+d*(n-1);
		
		System.out.println(result);
		
	}

}
